import "./App.css";
import styled from "styled-components";
import Button from "./components/Button/ButtonStyle";

//1.adım:ana dosya içinde css yazilabilir  veya başka dosyadan  import edilebilir.
const Wrapper = styled.div` 
  width: 100%;
  height: 100vh;
  border: 2px solid red;
`;
const SingleDiv = styled.div`
  border: 2px solid green;
  width: 100%;
  height: 200px;
`;

const Ptag = styled.p`
  width: 300px;
  background: orange;
`;

const Heading = styled.h1`
  font-size: ${props => props.size}px;
  color: ${props => props.color};
  &:hover{
    background:${props=>props.background}
  }
`;



function App() {
  return (
    <Wrapper>
    <Heading size="22px" color="purple" background="yello">
      E-commerce
    </Heading>
      <SingleDiv>
        <Ptag>Lorem, ipsum.</Ptag>
      </SingleDiv>
     <SingleDiv>
     <Ptag>Lorem ipsum dolor sit amet.</Ptag>
      <Button primary >
      Submit
      </Button>

      <Button colorful >
      Save
      </Button>
     </SingleDiv>
    </Wrapper>
  );
}

export default App;
