import "./App.css";
import styled from "styled-components";

const Wrapper = styled.div`
width:100%;
height:100vh;
border:2px solid red;
`;
const SingleDiv=styled.div`
border:2px solid green;
width:100%;
height:200px;
`;

const Ptag = styled.p`
 width:300px;
 background:orange;
`;

const Button = styled.button`
  background-color: #4caf50;
  border: none;
  color: white;
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin: 4px 2px;
  cursor: pointer;
`;

function App() {
  return (
    <Wrapper>
<SingleDiv>
<Ptag>Lorem, ipsum.</Ptag>
</SingleDiv>

      <Ptag>Lorem ipsum dolor sit amet.</Ptag>
      <Button>Submit</Button>
    </Wrapper>
  );
}

export default App;
