import styled from "styled-components";
const Button = styled.button`
    background-color: ${props => props.primary ? '#4caf50' : '#2196f3'}; {/**Button primary özelliği içeriyorsa ve içermiyorsa  durumları */}
  border: none;
  color: ${props=>props.colorful? "yellow":"white"};
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin: 4px 2px;
  cursor: pointer;
`;
export default Button;
