import "./App.css";
import styled from "styled-components";
import Button from "./components/Button/ButtonStyle";
import React,{useState} from "react";

//1.adım:ana dosya içinde css yazilabilir  veya başka dosyadan  import edilebilir.
const Wrapper = styled.div` 
  width: 100%;
  height: 100vh;
  border: 2px solid red;
`;
const SingleDiv = styled.div`
  border: 2px solid green;
  width: 100%;
  height: 200px;
`;

const Ptag = styled.p`
  width: 300px;
  background: orange;
`;

const Input = styled.input`
  width: 300px;
  font-size:12px;
  border:10px 10px;
`;


const Heading = styled.h1`
  font-size: ${props => props.size}px;
  color: ${props => props.color};
  &:hover{
    background:${props=>props.background}
  }
`;

const  change=()=>{
  setSize(11)
}

function App() {
  const [size,setSize]=useState(16);
  return (//2
    <Wrapper>
    <Heading size={size} color="purple" background="yellow" >
      E-commerce
    </Heading>
      <SingleDiv>
        <Ptag>Lorem, ipsum.</Ptag>
        <Heading color="red">Change E commerce Size</Heading>
        <Input value={size} onChange={(e)=>setSize(e.target.value)}/>
        <Button onClick={change}>Save</Button>
      </SingleDiv>
     <SingleDiv>
     <Ptag>Lorem ipsum dolor sit amet.</Ptag>
      <Button primary >
      Submit
      </Button>

      <Button colorful >
      Save
      </Button>
     </SingleDiv>
    </Wrapper>
  );
}

export default App;
