width:200px;
height:300px;
border:4px solid red;
`;

const Ptag=styled.p`
background:yellow;
`;

const Title = styled.title`
  font-size: 2rem;
  color: green;
  border:4px solid red;

`;

const Button = styled.button`
  background-color: #4CAF50;
  border: none;
  color: white;
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin: 4px 2px;
  cursor: pointer;
`;

function App() {
  return (
    <div  className="App">
  <Wrapper>
<div>Hello</div>
  </Wrapper>
  <Ptag>
   Lorem ipsum dolor sit amet.
  </Ptag>
  <Button>
  Submit
  </Button>
    </div>
  );
}

export default App;
