import "./App.css";
import styled from "styled-components";
import Button from "./components/Button/ButtonStyle";
import React, { useState } from "react";
import InputAttr from "./components/InputStyle";
import {Spinner} from "./components/AnimationStyle";
import {AnimatedLogo} from "./components/AnimationStyle";

//1.adım:ana dosya içinde css yazilabilir  veya başka dosyadan  import edilebilir.
const Wrapper = styled.div`
  width: 100%;
  height: 150vh;
  border: 2px solid red;
`;
const SingleDiv = styled.div`
  border: 2px solid green;
  width: 100%;
  height: 250px;
`;

const Ptag = styled.p`
  width: 300px;
  background: orange;
`;

const Input = styled.input`
  width: 300px;
  font-size: 12px;
  border: 10px 10px;
`;

const Heading = styled.h1`
  font-size: ${(props) => props.size}px;
  color: ${(props) => props.color};
  &:hover {
    background: ${(props) => props.background};
  }
`;

function App() {
  const [size, setSize] = useState(24);
  return (
    //2
    <Wrapper>
      <Heading size={size} color="purple" background="yellow">

        E-commerce{" "}
      </Heading>
      <SingleDiv>
        <Ptag>Lorem, ipsum.</Ptag>
        <Heading color="red">Change E commerce Font-Size</Heading>
        <Input value={size} onChange={(e) => setSize(e.target.value)} />
      </SingleDiv>

      <SingleDiv>
        <Ptag>Buttons</Ptag>
        <Button primary >
          Submit
        </Button>
        <Button color="red">Save</Button>
      </SingleDiv>
      <SingleDiv>
        <Heading>Passing Props and Adding Attributes</Heading>
        <InputAttr type="text"  required={true} placeholder="Enter a Username" placeholdercolor="green" />
      </SingleDiv>
      <SingleDiv>
        <Heading>Animations</Heading>
        <Spinner seconds="1"/>
        <br />
        <AnimatedLogo seconds="1" src="https://www.logodesignlove.com/wp-content/uploads/2022/11/carebibi-logo-01-1200x1200.jpeg" alt="logo"/>
      </SingleDiv>
    </Wrapper>
  );
}

export default App;
