import styled from 'styled-components';

const InputAtrb = styled.input.attrs(props => ({//elementin attributelerini dinamik olarak degistirebilirsin.
  type: props.type || 'text',
  required: true
}))`
  border: none;
  border-bottom: 2px solid black;
  font-size: 16px;
  padding: 5px;
  ::placeholder {
    color: ${props => props.placeholdercolor || "grey"};
  }
`;

export default InputAttr;
